import React, { useEffect } from "react";
const { kakao } = window;
//! import axios from "axios";

export default function MapPage({ logo }) {
  useEffect(() => {
    const mapContainer = document.getElementById('map'), // 지도를 표시할 div 
      mapOption = {
        center: new kakao.maps.LatLng(33.450701, 126.570667), // 지도의 중심좌표
        level: 3 // 지도의 확대 레벨
      };

    // 지도를 생성합니다    
    const map = new kakao.maps.Map(mapContainer, mapOption);

    // 주소-좌표 변환 객체를 생성합니spectacle
    function locationLoadSuccess(pos) {
      // 지도에 마커를 생성하고 표시한다
      const marker = new kakao.maps.Marker({
        position: new kakao.maps.LatLng(37.56748, 127.01693), // 마커의 좌표
        draggable: true, // 마커를 드래그 가능하도록 설정한다
        map: map // 마커를 표시할 지도 객체
      });

      // 마커 위에 표시할 인포윈도우를 생성한다
      const infowindow = new kakao.maps.InfoWindow({
        content: '<div style="padding:5px;">인포윈도우 :D</div>' // 인포윈도우에 표시할 내용
      });

      // 인포윈도우를 지도에 표시한다
      infowindow.open(map, marker);
    }

    function locationLoadError(pos) {
      alert('위치 정보를 가져오는데 실패했습니다')
    }

    function getCurrentPosBtn() {
      navigator.geolocation.getCurrentPosition(locationLoadSuccess, locationLoadError)
    };
  })




  //   console.log(kakao);
  return (
    <div className="main">
      <section className="top">상단 bar</section>
      <body className="inner">
        <div className="side">
          <div className="myinfo">내정보</div>
          <ul>
            <li>내 위치</li>
            <button type="button" onClick={getCurrentPosBtn}>내 현재 위치</button>
            <li>친구1</li>
            <li>친구2 (map함수)</li>
          </ul>
          <button>re meet</button>
        </div>
        <div className="map">
          <div>
            <div id="map" style={{ width: "500px", height: "400px" }}></div>
          </div>
        </div>
      </body>
    </div>
  );
}
