import React, { useState, useEffect, useRef } from "react";
//! import axios from "axios";

export default function Login({ logo }) {
  const modalEl = useRef(); //
  const [isOpen, setOpen] = useState(false);

  const handleModalOpen = () => {
    setOpen(true);
  };

  const handleClickOutside = ({ target }) => {
    if (isOpen && !modalEl.current.contains(target)) setOpen(false);
  };

  useEffect(() => {
    window.addEventListener("click", handleClickOutside);
    return () => {
      window.removeEventListener("click", handleClickOutside);
    };
  }, []);

  return (
    <div>
      <center>
        {/* <form> */}
        <body className="inner-container">
          <section className="identity">
            <h1>로고 및 소개 영역</h1>
            <div className="intro-area">
              <img src={logo} alt="logo" id="logo-login" />
              <div>우리 가운데서 만나요!</div>
            </div>
          </section>
          <section className="inputArea">
            <h1>입력 영역</h1>
            <div id="input-container">
              <input
                className="page1"
                type="text"
                placeholder="Email address"
              />
              <input
                className="page1"
                type="password"
                placeholder="password"
              ></input>
              <button className="page1 button">LOGIN</button>
              <button className="page1 button" onClick={handleModalOpen}>
                SIGN UP
              </button>
              <div className="page1 button">아이디 / 비밀번호 찾기</div>
            </div>
          </section>
        </body>
        {/* </form> */}
        {isOpen && <Modal ref={modalEl}>...</Modal>}
      </center>
    </div>
  );
}
