/*global kakao*/ 
import React, { useEffect, useState } from "react";
import KakaoMap from "./KakaoMap.js"
import MapDiv from "./MapDiv";
const { kakao } = window;


export default function MapPage() {
  let curDeg = [37.365264512305174, 127.10676860117488];
  const [curAdd, setCurAdd] = useState("");
  const [isSearch, setIsSearch] = useState(false);
  const [address, setAddress] = useState("");

  useEffect(() => {
    KakaoMap();
  }, [])

  const searchInputOn = () => {
    setIsSearch(true);
  };

  const searchBtnOn = () => {
    setCurAdd(address);
  };

  const inputAdd = (e) => {
    setAddress(e.target.value);
  };



  return (
    <div className="main">
      <section className="top">상단 bar</section>
      <body className="inner">
        <div className="side">
          <div className="myinfo">내정보</div>
          {isSearch ? (
            <div>
              <input type="text" onChange={(e) => inputAdd(e)} />
              <button onClick={searchBtnOn}>검색</button>
            </div>
          ) : (
            <button onClick={searchInputOn}>주소 검색</button>
          )}
          <ul>
            <li>내 위치</li>
            <li>친구1</li>
            <li>친구2 (map함수)</li>
          </ul>
          <button>re meet</button>
        </div>
        <div className="map">
          <div>
            <div id="map" style={{ width: "500px", height: "400px" }}>
            <div>
            {/* <div id="map"></div> */}
            <MapDiv curAdd={curAdd} />
          </div>
            </div>
          </div>
        </div>
      </body>
    </div>
  );
}
