import React, { useState, useEffect, useRef } from "react";
import Modal from "./Modal";
//! import axios from "axios";

export default function Login({ logo }) {
  const modal = document.getElementById("login-modal");
  const [isOpen, setOpen] = useState(false);

  //! handleClickOutside Advanced

  const handleModalOpen = () => {
    setOpen(!isOpen);
  };

  const handleClickOutside = ({ target }) => {
    if (isOpen && !modalEl.current.contains(target)) {
      setOpen(false);
      console.log(setOpen);
    }
  };

  window.onclick = function (event) {
    if (event.target == modal) {
      setOpen(false);
    }
  };
  return (
    <div>
      <center>
        {/* <form> */}
        <body className="inner-container">
          <section className="identity">
            <h1>로고 및 소개 영역</h1>
            <div className="intro-area">
              <img src={logo} alt="logo" id="logo-login" />
              <div>우리 가운데서 만나요!</div>
            </div>
          </section>
          <section className="inputArea">
            <h1>입력 영역</h1>
            <div id="input-container">
              <input
                className="page1"
                type="text"
                placeholder="Email address"
              />
              <input
                className="page1"
                type="password"
                placeholder="password"
              ></input>
              <button className="page1 button">LOGIN</button>
              <button className="page1 button" onClick={handleModalOpen}>
                SIGN UP
              </button>
              <div className="page1 button">아이디 / 비밀번호 찾기</div>
            </div>
          </section>
        </body>
        {/* </form> */}
        {isOpen ? (
          <Modal
            id="login-modal"
            handleModalOpen={handleModalOpen}
            ref={modalEl}
          />
        ) : null}
      </center>
    </div>
  );
}
