import React, { useState } from "react";
//! import axios from "axios";
import Modal from "./modal";

export default function Login({ logo }) {
  const [enterUser, setEnterUser] = useState(false);
  const handleSignUp = () => {
    setEnterUser(!enterUser);
  };

  return (
    <div>
      <center>
        <form>
          <body class="inner-container">
            <section class="identity">
              <h1>로고 및 소개 영역</h1>
              <div class="intro-area">
                <img src={logo} alt="logo" id="logo-login" />
                <div>우리 가운데서 만나요!</div>
              </div>
            </section>
            <section class="inputArea">
              <h1>입력 영역</h1>
              <div id="input-container">
                <input class="page1" type="text" placeholder="Email address" />
                <input
                  class="page1"
                  type="password"
                  placeholder="password"
                ></input>
                <button class="page1 button">LOGIN</button>
                <button class="page1 button" onClick={handleSignUp}>
                  SIGN UP
                </button>
                <div class="page1 button">아이디 / 비밀번호 찾기</div>
              </div>
            </section>
          </body>
        </form>
        {/* 모달창 */}
        <Modal />
        {enterUser ? <Modal /> : null}
      </center>
    </div>
  );
}
