import React, { useEffect } from "react";
const { kakao } = window;
//! import axios from "axios";

export default function MapPage({ logo }) {
    useEffect(() => {
        var container = document.getElementById("map");
        var options = {
            center: new kakao.maps.LatLng(37.365264512305174, 127.10676860117488),
            level: 3,
            mapTypeId: kakao.maps.MapTypeId.ROADMAP // 지도종류
        };

        var map = new kakao.maps.Map(container, options);
        var markerPosition = new kakao.maps.LatLng(
            37.365264512305174,
            127.10676860117488
        );
        function locationLoadSuccess(pos) {
            // 현재 위치 받아오기
            var currentPos = new kakao.maps.LatLng(pos.coords.latitude, pos.coords.longitude);

            // 지도 이동(기존 위치와 가깝다면 부드럽게 이동)
            map.panTo(currentPos);

            // 마커 생성
            var marker = new kakao.maps.Marker({
                position: currentPos
            });

            // 기존에 마커가 있다면 제거
            marker.setMap(null);
            marker.setMap(map);
        };

        function locationLoadError(pos) {
            alert('위치 정보를 가져오는데 실패했습니다.');
        };

        // 위치 가져오기 버튼 클릭시
        function getCurrentPosBtn() {
            navigator.geolocation.getCurrentPosition(locationLoadSuccess, locationLoadError);
        };
    }, []);
    return (
        <div className="main">
            <div className="green-nav">
                <div>MTM</div>
                <div className="right">내정보</div>
            </div>
            {/* <div className="myinfo">내정보</div> */}
            <button id="getMyPositionBtn">내 위치 가져오기</button>
            <body className="inner">
                <div className="side">
                    <ul>
                        <li>친구1</li>
                        <li>친구2 (map함수)</li>
                    </ul>
                    <button>re meet</button>
                </div>
                <div className="map">
                    <div>
                        <div id="map" style={{ width: "1300px", height: "500px" }}></div>
                    </div>
                </div>
            </body>
        </div>
    );
}
