import React, { useEffect, useState } from "react";
import axios from "axios";
import * as handleSignout from '../App.js';
import userInfo from "../dummy.js";
// 아이디 닉네임 비밀번호 비밀번호 확인 이름 휴대폰

export default function MyProfileModal({ myProfileModalOff }) {
  const [findInfo, setfindInfo] = useState({
    image: '',
    email: '',
    userName: '',
    nickName: '',
    password: '',
    phone: ''
  });
  const myInfo = async () => {
    await axios.get('https://localhost:4000/auth').then((res) => {
      setfindInfo(res.data.data.needInfo);
      //console.log(findInfo);
    });
  };

  useEffect(() => {
    return () => setfindInfo(null);
  }, []);

  return (
    <div className="modal">
      <div className="close-btn" onClick={myProfileModalOff}>
        X
      </div>
      <div onClick={myInfo}>회원 정보</div>
      <div>사진{findInfo.image}</div>
      <fieldset>
        <span>이메일: {findInfo.email}</span>
      </fieldset>
      <fieldset>
        <span>이름: {findInfo.userName}</span>
      </fieldset>
      <fieldset>
        <input className="info-modal-list" type="number" type="text" placeholder={findInfo.nickName}></input>
      </fieldset>
      <fieldset>
        <input className="info-modal-list" type="password"  placeholder="password"></input>
      </fieldset>
      <fieldset>
        <input className="info-modal-list" type="password"  placeholder="password confirm"></input>
      </fieldset>
      <fieldset>
        <input className="info-modal-list" type="number" type="text" placeholder={findInfo.phone}></input>
      </fieldset>
      <button className="modal-btn">회원정보 수정</button>
      <div id="myinfo-btn2">
      <span className="myinfo-btn" onClick={() => handleSignout}>로그아웃</span>
      <span> / </span>
      <span className="myinfo-btn">회원탈퇴</span>
      </div>
    </div>
  );
}
