/*global kakao*/
import React, { useEffect } from "react";
const { kakao } = window;
//! import axios from "axios";
// 아이디 닉네임 비밀번호 비밀번호 확인 이름 휴대폰

export default function MapDiv({ curAdd }) {
    //   console.log(curAdd);
    let add = curAdd ? curAdd : "태평로1가 31";
    //   console.log(add);

    useEffect(() => {
        console.log(add, "여기");
        var mapContainer = document.getElementById("map"), // 지도를 표시할 div
            mapOption = {
                center: new kakao.maps.LatLng(33.450701, 126.570667), // 지도의 중심좌표
                level: 3, // 지도의 확대 레벨
            };

        // 지도를 생성합니다
        var map = new kakao.maps.Map(mapContainer, mapOption);

        // 주소-좌표 변환 객체를 생성합니다
        var geocoder = new kakao.maps.services.Geocoder();

        // 주소로 좌표를 검색합니다
        geocoder.addressSearch(add, function (result, status) {
            // 정상적으로 검색이 완료됐으면
            if (status === kakao.maps.services.Status.OK) {
                var coords = new kakao.maps.LatLng(result[0].y, result[0].x);

                // 결과값으로 받은 위치를 마커로 표시합니다
                var marker = new kakao.maps.Marker({
                    map: map,
                    position: coords,
                });

                // 인포윈도우로 장소에 대한 설명을 표시합니다
                var infowindow = new kakao.maps.InfoWindow({
                    content:
                        '<div style="width:150px;text-align:center;padding:6px 0;">여기</div>',
                });
                infowindow.open(map, marker);

                // 지도의 중심을 결과값으로 받은 위치로 이동시킵니다
                map.setCenter(coords);
            }
        });

        let marker = new kakao.maps.Marker({
            // 지도 중심좌표에 마커를 생성합니다 
            position: map.getCenter()
        });

        marker.setMap(map)
        // 지도에 클릭 이벤트를 등록합니다
        // 지도를 클릭하면 마지막 파라미터로 넘어온 함수를 호출합니다

        kakao.maps.event.addListener(map, 'click', function (mouseEvent) {
            // removeMarker(marker)
            // removeinfo(infos)	
            // 클릭한 위도, 경도 정보를 가져옵니다 
            let latlng = mouseEvent.latLng;

            // 마커 위치를 클릭한 위치로 옮깁니
            // removeMarker(marker)

            // marker.setPosition(latlng);
            // console.log(marker)

            let iwContent = '<div calssName="messageRemove" style="padding:5px;"><div>여기</div></div>',
                iwPosition = marker.setPosition(latlng),
                iwRemoveable = true;

            // let messageDiv = document.getElementsByClassName("messageRemove")
            // messageDiv.removeChild(messageDiv.firstChild)

            let infowindow = new kakao.maps.InfoWindow({
                position: iwPosition,
                content: iwContent,
                removable: iwRemoveable
            })

            marker.setPosition(latlng);

            // console.log(iwRemoveable)

            let message = '클릭한 위치의 위도는 ' + latlng.getLat() + ' 이고, ';
            message += '경도는 ' + latlng.getLng() + ' 입니다';

            // let resultDiv = document.getElementById('clickLatlng'); 
            // resultDiv.innerHTML = message;

            infowindow.open(map, marker)


        });

    }, [add]);

    return <div id="map"></div>;
}
